
// Special pages
exports.blockIE = function($P) {
  $P.render($P.views.blockIE);
}

exports.authenticate = function($P) {
  $P.name = 'authenticate.js';
  if (!$P.loggedIn) $P.render($P.views.login)
  else $P.res.redirect(Const.server);
}

exports.authThenRedirect = function($P,redirectTo) {
  $P.name = 'authenticate.js';
  if (!$P.loggedIn) $P.render($P.views.login)
  else $P.res.redirect(redirectTo);
}

exports.errorPage = function($P,errorMsg) {
  $P.exports.MID = 0;
  $P.name = 'error.js';
  $P.exports.errorMsg = errorMsg;
  $P.render();
}

